function add_tag(){t_name=$("#tag_name_input").val(),post_json=JSON.stringify({t_name:t_name}),api_url=api_server_url+"/api/async/v1/tags",$.ajax({url:api_url,method:"POST",data:post_json,contentType:"application/json;charset=utf-8",dataType:"json",error:function(t,a){alert(a)}})}function delete_tag(){t_name=$("#tag_name_input").val(),api_url=api_server_url+"/api/async/v1/tags?t_name="+t_name,$.ajax({url:api_url,method:"DELETE",contentType:"application/json;charset=utf-8",dataType:"json",success:function(t){1==t.code&&($("#tag_name_input").popover({title:"错误",content:t.msg,placement:"top",manual:!0}),$("#tag_name_input").popover("show"))},error:function(t){$("#tag_name_input").popover({title:"错误",content:t.msg,placement:"top"})}})}